//eslint-disable-next-line
import React, { useState, useEffect } from 'react';
import './App.css';
import Weather from './Weather.js';

const App = () => {

  const [weatherData, setWeather] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('seattle,us');

  useEffect(() => {
    getWeather();
  }, []);

  const APP_ID = '4d5ece97315492a895ae0fad7328bf6a';

  const getWeather = async () => {
    const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=seattle,us&appid=${APP_ID}`);
    const data = await response.json();
    setWeather(data);
    console.log(data);
  };

  const updateSearch = e => {
      setSearch(e.target.value);
  };

  const getSearch = e => {
    e.preventDefault();
    setQuery(search);
    setSearch(query);
  };


    return(
      <div className="App">
        <form className="search-form">
          <input 
            className="search-button"
            type="text"
            placeholder="Enter city, country code (eg: Seattle, US)"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
        <Weather />
      </div>
    );
};

export default App;
