import React, { useState, useEffect } from 'react';
import './App.css';
import Weather from './Weather.js';

const App = () => {

const [weathers, setRecipes] = useState([]);
const [search, setSearch] = useState('');
const [query, setQuery] = useState('banana');

useEffect(() => {
  getWeather();
}, []);

const APP_ID = 'c9b8ede06d531251b377d89a26d6a87a';

const getWeather = async () => {
  const response = await fetch(`api.openweathermap.org/data/2.5/weather?q=${query}&appid=${APP_ID}`);

  const data = await response.json();

  console.log(data);
};

  return(
    <div className="App">
      <form className="search-form">
        <input 
          className="search-button"
          type="text"
          placeholder="Enter city, country code (eg: Seattle, US)"
        />
        <button type="submit" className="search-button">Search</button>
      </form>

    </div>
  );
};

export default App;
